    memcpy(pars_t, pars, sizeof(double)*n_pars);
    pars_t[1] = pars[1] * f_star(t);
    pars_t[2] = pars[2] * R_vir(t) / R_vir0;

    miyamotonagai_gradient(t, pars_t, q, n_dim, grad);
